import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { FormBuilder, FormGroup } from '@angular/forms';
import { BrowserModule } from '@angular/platform-browser';
import { ActivatedRoute, Router } from '@angular/router';
import { AppComponent } from '../app.component';
import { SQLService } from '../sql.service';
import Swal from 'sweetalert2';


@Component({

  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})



export class HomeComponent implements OnInit {

  form!: FormGroup; 
  dbname:any;
  user:any;
  password:any;
  host:any;
  unoptimizedQuery: any;

  optimized_query:any;
  optimized_time:any;
  unoptimized_query:any;
  unoptimized_time:any;
  result:any;

  constructor(private fb: FormBuilder,private sqlService: SQLService,private http: HttpClient, private router: Router,private route: ActivatedRoute) 

  {}

  ngOnInit() {

  }

  connect() {
    this.sqlService.connectToDatabase(this.dbname, this.user, this.password, this.host)
      .subscribe((response : any) => {
        console.log(response)
      });
      Swal.fire({
        position: 'top-end',
        icon: 'success',
        title: 'Successfully established a connection to the database.',
        showConfirmButton: false,
        timer: 1500
      });
  }

  // optimize() {
  //   this.sqlService.optimizeQuery(this.unoptimizedQuery)
  //     .subscribe(response => console.log(response));
  // }

  // optimize() {
  //   this.sqlService.optimizeQuery(this.unoptimizedQuery)
  //     .subscribe(response => {
  //       this.optimizationResult = response;
  //       console.log(response);
  //     });

  // optimize() {
  //   this.sqlService.optimizeQuery(this.unoptimized_query)
  //     .subscribe(response => {
  //       this.optimized_query = response.optimized_query;
  //       this.optimized_time = response.optimized_time;
  //       this.unoptimized_query = response.unoptimized_query;
  //       this.unoptimized_time = response.unoptimized_time;
  //     });



  optimize() {
    this.sqlService.optimizeQuery(this.unoptimizedQuery)
    .subscribe((response : any) => {
    this.optimized_query = response.optimized_query;
    this.optimized_time = response.optimized_time;
    this.unoptimized_query = response.unoptimized_query;
    this.unoptimized_time = response.unoptimized_time;
    this.result = response.result;


      console.log(response)
    });
  }





  }





<nav>
  <a href="/home">Home</a> |
  <a href="/about">About</a> |
  <a href="/contact">Contact</a>
</nav>
<div class="background">
<div style="display: flex; flex-direction: row;">
  <!-- Left side content -->
  <div style="width: 50%;">
    <div class="container">
      <h1>Connect to Database</h1>
      <form (submit)="connect()">
        <div class="form-group">
          <label for="dbname">Database Name</label>
          <input type="text" class="form-control" id="dbname" [(ngModel)]="dbname" name="dbname">
        </div>
        <div class="form-group">
          <label for="user">Username</label>
          <input type="text" class="form-control" id="user" [(ngModel)]="user" name="user">
        </div>
        <div class="form-group">
          <label for="password">Password</label>
          <input type="password" class="form-control" id="password" [(ngModel)]="password" name="password">
        </div>
        <div class="form-group">
          <label for="host">Host</label>
          <input type="text" class="form-control" id="host" [(ngModel)]="host" name="host">
        </div>
        <button type="submit" class="btn btn-primary">Connect</button>
      </form>

      <h1>Optimize Query</h1>
      <form (submit)="optimize()">
        <div class="form-group">
          <label for="unoptimized_query">Unoptimized Query</label>
          <textarea id=textarea0 class="form-control" id="unoptimized_query" [(ngModel)]="unoptimizedQuery" name="unoptimized_query" rows="3"></textarea>
        </div>
        <button type="submit" class="btn btn-primary">Optimize</button>
      </form>
    </div>
  </div>
  <!-- Right side content -->

  <div style="width: 50%;">
    <div class="container" style="display: flex; flex-direction: column;">
      <h1>optimization result</h1>
      <textarea disabled id="textarea1" rows="8" cols="80">
        Optimized Query: {{optimized_query}}
        Optimized Time: {{optimized_time}}
        Unoptimized Query: {{unoptimized_query}}
        Unoptimized Time: {{unoptimized_time}}

      </textarea>

  </div>

</div>

</div>
<footer>
  <div class="footer-container">
    <p>Copyright 2021 SQL OPTIMIZER</p>
    <p>Contact: <a href="mailto:you@example.com">mdelhaous@gmail.com</a></p>
  </div>
</footer>
